package java_programs;

public class Logical_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a= 5;
	    int b= 1;
	    int c= 10;
	     System.out.println(a & b);
	     System.out.println(a | b);
	     System.out.println(a ^ b);
	     System.out.println(a << b);
	     System.out.println(a >> b);
	     System.out.println(a >>> b);
	     

	}

}
