package java_programs;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Arithmetic operators
       
	 int a=20;
	 int b=30;
	 float c=10f;
	 float d=20f;
	 
	 System.out.println(a+b);
	 System.out.println(a-b);
	 System.out.println(a*b);
	 System.out.println(a/b);
	 System.out.println(c/d);
	 
	 }

}
